package loop;

public class nestedloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=3;i<=5;i++)
		{
			System.out.println("value of i:"+i);
			for(int j=1;j<=i;j++)
			{
				System.out.println("value of j:"+j);
				for(int k=1;k<=j;k++)
						
						{
					System.out.println("value of k:"+k);
					break;
					
				
			}
			
			
		}

	}
	}}


